panel.onenddrag:
Set('_target_x', inventory_panel_{slot}_X);
Set('_target_y', inventory_panel_{slot}_Y);
If(param0 == _target_x and param1 == _target_y, Split(Sell({slot}, 1), PlaySound('/shared/sounds/ui/sell.mp3')));
SetAbsoluteX(_target_x);
SetAbsoluteY(_target_y);

Set('_target_x', inventory_panel_{slot}_X);
Set('_target_y', inventory_panel_{slot}_Y);
If(param0 == _target_x and param1 == _target_y, Split(Sell({slot}, 1), PlaySound('/shared/sounds/ui/sell.mp3')));
SetAbsoluteX(_target_x);
SetAbsoluteY(_target_y);

button.ontrigger:
if(StringLength(param) > 0, Split(
					SetOnclick('PlaySound(\'/shared/sounds/ui/gold.mp3\'); Purchase(\'' # param # '\');'),
					SetOnmouseover('
						PlaySound(\'/shared/sounds/ui/button_over_small.mp3\');
						Call(\'loadout_item_tooltip_name\', \'SetText(*(\\\'' # param # '_Name\\\'));\');
						Call(\'loadout_item_tooltip_cost\', \'SetText(*(\\\'' # param # '_Cost\\\'));\');
						Call(\'loadout_item_tooltip_description\', \'SetText(*(\\\'' # param # '_Description\\\'));\'):
						Call(\'loadout_item_tooltip_prerequisite\', \'SetText(GetPrereqDetails(*(\\\'' # param # '_Prerequisite\\\')));\'):
						ShowWidget(\'item_store\');
						showwidget(\'item_store_hightlight\');
						ShowWidget(\'loadout_item_tooltip_costheader\');
						ShowWidget(\'loadout_item_tooltip_prerequisite\');
						ShowWidget(\'loadout_item_tooltip_prerequisiteheader\');
						ShowWidget(\'loadout_item_tooltip_cost\');
						ShowWidget(\'loadout_item_tooltip\');
						HideWidget(\'loadout_chat_panel\');')),
					Split(SetOnmouseover('showwidget(\'item_store\');'), SetOnclick('')));
